let dynamicMask = IMask(
    document.getElementById('pin'),
    {
        mask: [
            {
                mask: '+(381) 000-000-00'
            },
            {
                mask: /^\S*@?\S*$/
            }
        ]
    });

function check() {
    if ($('#pin').val() != '+(381) 111-111-11')
        $('#submit-btn').attr('disabled', 'disable');
    else
        $('#submit-btn').removeAttr('disabled');
}
